Bash(powershell -Command "
$ftpServer = ''ftp://ftp.elektrokombinacija.com''
$ftpUser = ''bojan@elektrokombinacija.com''
$ftpPass = ''mSczA3mwbGsTiFx''
$localPath = ''D:/work/autobusi-punjaci/web/dist''
$remotePath = ''/public_html''

function Upload-FtpFile\($localFile, $remoteFile\) {
    $webclient = New-Object System.Net.WebClient
    $webclient.Credentials = New-Object System.Net.NetworkCredential\($ftpUser, $ftpPass\)
    try {
        $uri = $ftpServer + $remoteFile
        $webclient.UploadFile\($uri, $localFile\)
        Write-Host ""Uploaded: $remoteFile""
    } catch {
        Write-Host ""Failed: $remoteFile - $_""
    }
}

# Upload root files
Get-ChildItem -Path $localPath -File | ForEach-Object {
    Upload-FtpFile $_.FullName \($remotePath + ''/'' + $_.Name\)
}

# Upload assets folder
Get-ChildItem -Path ""$localPath/assets"" -File | ForEach-Object {
    Upload-FtpFile $_.FullName \($remotePath + ''/assets/'' + $_.Name\)
}

Write-Host ''Deploy complete!''
")

